test-code.code:
class BadExample {
    private final Lock lock = new ReentrantLock();

    public void foo() {
        lock.lock();
        System.out.println("Foo");
    }

}

class GoodExample {
    private final Lock lock = new ReentrantLock();

    public void foo() {
        lock.lock();
        try {
            System.out.println("Foo");
        } finally {
            lock.unlock();
        }
    }

}
